import React, { useState } from 'react';

function Table({ data }) {
    const [selectedRow, setSelectedRow] = useState(null);
  
    const handleRowClick = (index) => {
      setSelectedRow(index);
    };
  
    return (
      <table>
        <thead>
            <tr>

                <th>Username</th>
                <th>Title</th>
                <th>Description</th>
                <th>Price</th>
                <th>Category</th>
                <th>Created</th>

            </tr>
            </thead>
        <tbody>
          {data.map((row, index) => (
            <tr
              key={index}
              onClick={() => handleRowClick(index)}
              className={selectedRow === index ? 'selected' : ''}
            >
              <tbody>
                {data.map((row, index) => (
                    <tr
                    key={index}
                    onClick={() => handleRowClick(index)}
                    className={selectedRow === index ? 'selected' : ''}
                    >
                    <td>{row.username}</td>
                    <td>{row.name}</td>
                    <td>{row.price}</td>
                    </tr>
                ))}
                </tbody>
            </tr>
          ))}
        </tbody>
      </table>
    );
  }

export default Table;
