import React from 'react'
import axios from "axios";
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Navbar.css';
import CheckAuthButton from '../CheckAuthButton';

const Navbar = () => {
  
  const [username, setUsername] = useState('Unknown');
  const [first_name, setFirstName] = useState('');
  const [last_name, setLastName] = useState('');

  const navigate = useNavigate();

  useEffect(() => {

    const token = localStorage.getItem('access_token');

    const storedUserData = localStorage.getItem('user_data');

    if (storedUserData) {

        const userData = JSON.parse(storedUserData);

        setUsername(userData.username);
        setFirstName(userData.first_name);
        setLastName(userData.last_name);

    }

    axios.get('http://127.0.0.1:8000/get-account-data/', {

        headers: {

          'Authorization': `Bearer ${token}`
        }

      })
      .then(response => {
        
      // If request is successful, populate state
        if (response.status === 200) {

          console.log("response data:", response.data);

          setUsername(response.data.username);
          setFirstName(response.data.first_name);
          setLastName(response.data.last_name);

        }
      })

  }, []);

  const sign_out = () => {

      axios.post('http://127.0.0.1:8000/logout/', {}, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('access_token')}`
        }
      })
      .then(() => {
        localStorage.removeItem("access_token");
        navigate('/');
      })
      .catch(error => {
        console.error("Error during sign-out!", error);
        localStorage.removeItem("access_token");
        navigate('/');
      });
  
    };

  return (
    <nav class="nav">
        <a href = "/home" class="nav-element">home</a>
        <h1>Signed in as {username}</h1>
        <ul>
            <button onClick={sign_out} className='signout'>Sign Out</button>
            <CheckAuthButton/>
        </ul>
    </nav>
  )
}

export default Navbar
